using System;
using System.Collections.Generic;
using System.Data.SQLite;
using System.IO;
using System.Text.Json;
using System.Threading.Tasks;
using CheckmateDeck.Models;
using Microsoft.Extensions.Logging;

namespace CheckmateDeck.Services
{
    public class DatabaseService : IDatabaseService
    {
        private readonly string _connectionString;
        private readonly ILogger<DatabaseService> _logger;

        public DatabaseService(ILogger<DatabaseService> logger)
        {
            _logger = logger;
            try
            {
                string dbPath = Environment.GetEnvironmentVariable("CHECKMATE_DB_PATH");
                if (string.IsNullOrEmpty(dbPath))
                {
                    throw new InvalidOperationException("CRITICAL: CHECKMATE_DB_PATH environment variable is not set.");
                }

                if (!File.Exists(dbPath))
                {
                    _logger.LogWarning("Database file does not exist at the path specified by CHECKMATE_DB_PATH: {Path}", dbPath);
                }

                _connectionString = $"Data Source={dbPath};Version=3;Read Only=True;";
                _logger.LogInformation("DatabaseService initialized with path from environment variable.");
            }
            catch (Exception ex)
            {
                _logger.LogCritical(ex, "Failed to initialize DatabaseService from environment variable.");
                throw;
            }
        }

        public async Task<List<DisplayRace>> GetQualifiedRacesAsync()
        {
            var races = new List<DisplayRace>();
            try
            {
                await using (var connection = new SQLiteConnection(_connectionString))
                {
                    await connection.OpenAsync();
                    // CRITICAL FIX: Query 'live_races' table directly with a WHERE clause.
                    var command = new SQLiteCommand(@"
                        SELECT race_id, track_name, race_number, post_time, checkmate_score
                        FROM live_races
                        WHERE qualified = 1
                        ORDER BY checkmate_score DESC, post_time ASC",
                        connection);

                    await using (var reader = await command.ExecuteReaderAsync())
                    {
                        while (await reader.ReadAsync())
                        {
                            races.Add(new DisplayRace
                            {
                                RaceId = reader.GetString(0),
                                TrackName = reader.GetString(1),
                                RaceNumber = reader.GetInt32(2),
                                PostTime = reader.GetDateTime(3),
                                CheckmateScore = reader.GetDouble(4)
                            });
                        }
                    }
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Failed to get qualified races from database.");
            }
            return races;
        }

        public async Task<List<AdapterStatusDisplay>> GetAdapterStatusesAsync()
        {
            var statuses = new List<AdapterStatusDisplay>();
            try
            {
                await using (var connection = new SQLiteConnection(_connectionString))
                {
                    await connection.OpenAsync();
                    var command = new SQLiteCommand("SELECT adapter_name, status, last_run, races_found FROM adapter_status ORDER BY last_run DESC", connection);
                    await using (var reader = await command.ExecuteReaderAsync())
                    {
                        while (await reader.ReadAsync())
                        {
                            statuses.Add(new AdapterStatusDisplay
                            {
                                AdapterName = reader.GetString(0),
                                Status = reader.GetString(1),
                                LastRun = reader.GetDateTime(2),
                                RacesFound = reader.GetInt32(3)
                            });
                        }
                    }
                }
            }
            catch (Exception ex)
            {
                // Replace with a proper logging mechanism
                Console.WriteLine($"[ERROR] Failed to get adapter statuses: {ex.Message}");
            }
            return statuses;
        }
    }
}

attic/desktop_app/ViewModels/MainViewMod:
using System;
using System.Collections.ObjectModel;
using System.ComponentModel;
using System.Linq;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Threading;
using CheckmateDeck.Models;
using CheckmateDeck.Services;

namespace CheckmateDeck.ViewModels
{
    public class MainViewModel : INotifyPropertyChanged
    {
        private readonly IDatabaseService _dbService; // Program to the interface
        private readonly DispatcherTimer _timer;

        public ObservableCollection<DisplayRace> QualifiedRaces { get; set; }
        public ObservableCollection<AdapterStatusDisplay> AdapterStatuses { get; set; }

        // Inject the dependency via the constructor
        public MainViewModel(IDatabaseService dbService)
        {
            _dbService = dbService; // No more "new DatabaseService()"
            QualifiedRaces = new ObservableCollection<DisplayRace>();
            AdapterStatuses = new ObservableCollection<AdapterStatusDisplay>();
            _timer = new DispatcherTimer
            {
                Interval = TimeSpan.FromSeconds(15)
            };
            _timer.Tick += async (sender, e) => await RefreshDataAsync();
            _timer.Start();
            Task.Run(async () => await RefreshDataAsync());
        }

        private async Task RefreshDataAsync()
        {
            var races = await _dbService.GetQualifiedRacesAsync();
            var statuses = await _dbService.GetAdapterStatusesAsync();

            // Ensure UI updates happen on the main dispatcher thread
            Application.Current.Dispatcher.Invoke(() =>
            {
                // --- Granular Update for Races ---
                var racesToRemove = QualifiedRaces.Where(qr => !races.Any(r => r.RaceId == qr.RaceId)).ToList();
                foreach (var race in racesToRemove) QualifiedRaces.Remove(race);

                foreach (var race in races)
                {
                    var existingRace = QualifiedRaces.FirstOrDefault(r => r.RaceId == race.RaceId);
                    if (existingRace != null)
                    {
                        // Update properties on existing object for a seamless refresh
                        existingRace.CheckmateScore = race.CheckmateScore;
                    }
                    else
                    {
                        QualifiedRaces.Add(race);
                    }
                }

                // --- Simple Replace for Statuses (often less performance-critical) ---
                AdapterStatuses.Clear();
                foreach (var status in statuses) AdapterStatuses.Add(status);
            });
        }

        public event PropertyChangedEventHandler? PropertyChanged;
    }
}

