{
  "name": "fortuna-faucet-desktop",
  "version": "1.0.0",
  "description": "The desktop shell for the Fortuna Faucet application.",
  "main": "main.js",
  "build": {
    "appId": "com.fortuna.ascended",
    "productName": "Fortuna Faucet",
    "files": [
      "main.js",
      "preload.js",
      "assets/**/*"
    ],
    "win": {
      "target": "msi",
      "icon": "assets/icon.ico"
    },
    "msi": {
      "oneClick": false,
      "allowToChangeInstallationDirectory": true
    }
  },
  "scripts": {
    "start": "electron .",
    "dist": "electron-builder"
  },
  "devDependencies": {
    "electron": "^28.0.0",
    "electron-builder": "^24.9.1"
  }
}
